package com.example.hw_3_3month;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.hw_3_3month.databinding.FragmentCarBinding;

import java.lang.reflect.Array;
import java.util.ArrayList;


public class CarFragment extends Fragment {

    private FragmentCarBinding binding;
    private ArrayList<CarModel> cars = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentCarBinding.inflate(inflater,container,false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        CarAdapter carAdapter = new CarAdapter(cars);
        binding.rvCar.setAdapter(carAdapter);
        initAdapter();
    }

    private void initAdapter() {
        cars.add(new CarModel("Mercedes","data:image/png;base64,",""));
        cars.add(new CarModel("Toyota","https://w7.pngwing.com/pngs/996/945/png-transparent-bmw-car-logo-bmw-logo-trademark-logo-car.png",""));
        cars.add(new CarModel("BMW","data:image/png;base64,",""));
        cars.add(new CarModel("Audi","https://assets.turbologo.ru/blog/ru/2019/12/18163141/logo-audi-about.png",""));
    }
}